import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useFormik } from 'formik';
import * as Yup from 'yup'; 

import "./login.style.css";
import { Input } from "../../components/input/Input";

const SignupSchema = Yup.object().shape({
    name: Yup.string().required('Name is required'),
    email: Yup.string().email('Invalid email').required('Email is required'),
    password: Yup.string().required('Password is required'),
    confirmPassword: Yup.string().oneOf([Yup.ref('password'), null], 'Passwords must match').required('Confirm Password is required')
});

export const LoginPage = () => {
    const [active, setActive] = useState(false);
    const navigate = useNavigate();

    const initialValues = {
        name: "",
        email: "",
        password: "",
        confirmPassword: ""
    };

    const { values, errors, touched, handleBlur, handleSubmit, handleChange } = useFormik({
        initialValues: initialValues,
        validationSchema: SignupSchema,
        onSubmit: (values) => {
            console.log("Form values:", values);
            localStorage.setItem('items', JSON.stringify(values));
            navigate("/dashboard");
        }
    });

    const handleActive = () => {
        setActive(!active);
    };

    return (
        <div className="login-container">
            <div className={`container ${active ? "active" : ""}`} id="container">
                <div className="form-container sign-up">
                    <form onSubmit={handleSubmit}>
                        <h1>Create Account</h1>
                        <div className="social-icons">
                            <Link className="icon"><i className="fa-brands fa-google-plus-g"></i></Link>
                            <Link className="icon"><i className="fa-brands fa-github"></i></Link>
                            <Link className="icon"><i className="fab fa-linkedin"></i></Link>
                        </div>
                        <span>or use your email for registration</span>
                        <Input
                            type="text"
                            name="name"
                            placeholder="Enter Name"
                            value={values.name}
                            onChange={handleChange}
                            onBlur={handleBlur}
                        />
                        {errors.name && touched.name && <span className="errorMsg">{errors.name}</span>}
                        <Input
                            type="email"
                            name="email"
                            placeholder="Email"
                            value={values.email}
                            onChange={handleChange}
                            onBlur={handleBlur}
                        />
                        {errors.email && touched.email && <span className="errorMsg">{errors.email}</span>}
                        <Input
                            type="password"
                            name="password"
                            placeholder="Password"
                            value={values.password}
                            onChange={handleChange}
                            onBlur={handleBlur}
                        />
                        {errors.password && touched.password && <span className="errorMsg">{errors.password}</span>}
                        <Input
                            type="password"
                            name="confirmPassword"
                            placeholder="Confirm Password"
                            value={values.confirmPassword}
                            onChange={handleChange}
                            onBlur={handleBlur}
                        />
                        {errors.confirmPassword && touched.confirmPassword && <span className="errorMsg">{errors.confirmPassword}</span>}
                        <button type="submit">Sign Up</button>
                    </form>
                </div>
                <div className="form-container sign-in">
                    <form onSubmit={handleSubmit}>
                        <h1>Sign In</h1>
                        <div className="social-icons">
                            <Link className="icon"><i className="fa-brands fa-google-plus-g"></i></Link>
                            <Link className="icon"><i className="fa-brands fa-github"></i></Link>
                            <Link className="icon"><i className="fab fa-linkedin"></i></Link>
                        </div>
                        <span>or use your email password</span>
                        <Input
                            type="email"
                            name="email"
                            placeholder="Email"
                            value={values.email}
                            onChange={handleChange}
                            onBlur={handleBlur}
                        />
                        {errors.email && touched.email && <span className="errorMsg">{errors.email}</span>}
                        <Input
                            type="password"
                            name="password"
                            placeholder="Password"
                            value={values.password}
                            onChange={handleChange}
                            onBlur={handleBlur}
                        />
                        {errors.password && touched.password && <span className="errorMsg">{errors.password}</span>}
                        <Link>Forget Your Password?</Link>
                        <button type="submit">Sign In</button>
                    </form>
                </div>
                <div className="toggle-container">
                    <div className="toggle">
                        <div className={`toggle-panel toggle-left ${active ? "" : "hidden"}`}>
                            <h1>Welcome Back!</h1>
                            <p>Enter your personal details to use all of site features</p>
                            <button onClick={handleActive}>Sign In</button>
                        </div>
                        <div className={`toggle-panel toggle-right ${active ? "hidden" : ""}`}>
                            <h1>Hello, Friend!</h1>
                            <p>Register with your personal details to use all of site features</p>
                            <button onClick={handleActive}>Sign Up</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};
