import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useFormik } from 'formik';

import "./login.style.css";

export const LoginPage = () => {
    const [active, setActive] = useState(false);
    const [userData,setUserData] = useState({name:"",email:"",password:""})
    const [LoggedInUser,setLoggedInUser] = useState({email:"",password:""})
    const initialValues = {
        name:"",
        email:"",
        password:"",
        confirmPassword:""
    }

    const {values,handleBlur,handleSubmit,handleChange} = useFormik({
        initialValues:initialValues,
        onSubmit:(values) => {
            console.log("vvvv",values)
        }
    })

    const handleActive = () => {
        setActive(!active);
    };

    const handleInput = (inputValue,name) => {
        if(name === "email"){
            setLoggedInUser({...LoggedInUser,email:inputValue})
        } else {
            setLoggedInUser({...LoggedInUser,password:inputValue})
        }
    }

    // const handleChange = (e) => {
    //     if(e.target.name === "name"){
    //        setUserData({...userData,name:e.target.value})
    //     } else if (e.target.name === "email"){
    //        setUserData({...userData,email:e.target.value})
    //     } else {
    //         setUserData({...userData,password:e.target.value})
    //     }
    // }

    // const handleSubmit = () => {
    //     alert("form submitted")
    // }

    return (
        <div className={`container ${active ? "active" : ""}`} id="container">
            <div className="form-container sign-up">
                <form onSubmit={handleSubmit}>
                    <h1>Create Account</h1>
                    <div className="social-icons">
                        <Link  className="icon"><i className="fa-brands fa-google-plus-g"></i></Link>
                        <Link  className="icon"><i className="fa-brands fa-facebook-f"></i></Link>
                        <Link  className="icon"><i className="fa-brands fa-github"></i></Link>
                        {/* <Link  className="icon"><i className="fa-brands fa-Linkedin-in"></i></Link> */}
                    </div>
                    <span>or use your email for registration</span>
                    <input type="text" name="name" placeholder="Name" onChange = {handleChange}/>
                    <input type="email" name="email" placeholder="Email" onChange = {handleChange}/>
                    <input type="password" name="password" placeholder="Password" onChange = {handleChange}/>
                    <input type="confirmPassword" name="confirmPassword" placeholder="confirmPassword" onChange = {handleChange}/>
                    <button type="submit">Sign Up</button>
                </form>
            </div>
            <div className="form-container sign-in">
                <form onSubmit={handleSubmit}>
                    <h1>Sign In</h1>
                    <div className="social-icons">
                        <Link  className="icon"><i className="fa-brands fa-google-plus-g"></i></Link>
                        <Link  className="icon"><i className="fa-brands fa-facebook-f"></i></Link>
                        <Link  className="icon"><i className="fa-brands fa-github"></i></Link>
                        {/* <Link  className="icon"><i className="fa-brands fa-Linkedin-in"></i></Link> */}
                    </div>
                    <span>or use your email password</span>
                    <input type="email" name="email" placeholder="Email" onChange={(e) => handleInput(e.target.value,e.target.name)}/>
                    <input type="password" name="password" placeholder="Password" onChange={(e) => handleInput(e.target.value,e.target.name)}/>
                    <Link >Forget Your Password?</Link>
                    <button type="submit">Sign In</button>
                </form>
            </div>
            <div className="toggle-container">
                <div className="toggle">
                    <div className={`toggle-panel toggle-left ${active ? "" : "hidden"}`}>
                        <h1>Welcome Back!</h1>
                        <p>Enter your personal details to use all of site features</p>
                        <button onClick={handleActive}>Sign In</button>
                    </div>
                    <div className={`toggle-panel toggle-right ${active ? "hidden" : ""}`}>
                        <h1>Hello, Friend!</h1>
                        <p>Register with your personal details to use all of site features</p>
                        <button onClick={handleActive}>Sign Up</button>
                    </div>
                </div>
            </div>
        </div>
    );
};
